"use strict";

const ynetPage = require('../pageobjects/ynetNews.page');
const {
  startStep,
  endStep,
  addStep
} = require('@wdio/allure-reporter').default;
describe('Articles Data', () => {
  it('Main Article Data', async () => {
    startStep('click on main article');
    await ynetPage.clickOnMainArticleButton();
    endStep();
    await ynetPage.printData();
  });
  it.only('Four Sub-Main Articles Data ', async () => {
    await ynetPage.dataFromMainSubTitleArticle();
  });
  it('Political News Data ', async () => {
    await ynetPage.dataFromPoliticalArticle();
  });
  it('Isreal News Data ', async () => {
    await ynetPage.dataFromIsrealNewsArticle();
  });
  it('World News Data ', async () => {
    await ynetPage.dataFromWorldNewsArticle();
  });
});
