"use strict";

const BasePage = require('../pageobjects/base.page');
const {
  startStep,
  endStep,
  addStep
} = require('@wdio/allure-reporter').default;
class ynetNewsPage {
  //////////////////////////data from article///////////////////////////////////////////////////

  get titleText() {
    return $("//*[@class='mainTitle'] |  //*[@class='BlogsPageTitle']");
  }
  get subTitleText() {
    return $("//div[@class='subTitleWrapper'] |  //*[@class='BlogsPageSubTitle']");
  }
  get summaryText() {
    return $$("//*[@class='text_editor_paragraph rtl']");
  }
  get imageText() {
    return $$("[id^='ReduxEditableImage_ArticleImageData']");
  }
  get dateTimeText() {
    return $(".DateDisplay");
  }
  get authorsText() {
    return $("//*[@class='authors ']/span[1]/a");
  }

  /////////////////////////////articles/////////////////////////////////////////////////

  get mainArticleButton() {
    return $("//*[@class='TopStory1280Componenta basic AuthorOnMode']");
  }
  get subArticlesButton() {
    return $$("//*[@class='YnetMultiStripComponenta oneRow']/div/div[1]/div[1]/div[1]");
  }
  get politicalAndSecurityButton() {
    return $$("(//*[@class='slotsContent'])[2]/div/div[@class='slotView']/div[1]/div[1]/a");
  }
  get isrealNewsButton() {
    return $$("(//*[@class='slotsContent'])[4]/div/div[@class='slotView']/div[1]/div[1]/a");
  }
  get worldNewsButton() {
    return $$("(//*[@class='slotsContent'])[5]/div/div[@class='slotView']/div[1]/div[1]/a");
  }

  /////////////popup between the pages//////////////////////////
  get popup() {
    return $("//*[@id='closeimg']");
  }

  //----------------------------------------------------------
  //Click
  //----------------------------------------------------------

  async clickOnMainArticleButton() {
    return await BasePage.clickButton(this.mainArticleButton);
  }
  async closePopup() {
    if (await this.popup.isExisting({
      timeout: 2000
    })) {
      await this.popup.click();
      await browser.pause(3000);
    }
  }

  //----------------------------------------------------------
  //Actions (get)
  //----------------------------------------------------------

  async getTitle() {
    startStep('print title text');
    endStep();
    return await BasePage.getText(this.titleText);
  }
  async getSubTitle() {
    startStep('print sub title text');
    endStep();
    return await BasePage.getText(this.subTitleText);
  }
  async getDate() {
    startStep('print date text');
    endStep();
    return await BasePage.getText(this.dateTimeText);
  }
  async getImg() {
    startStep('print img text');
    let list = await this.imageText;
    endStep();
    return await BasePage.getAtribute(list[0], 'src');
  }
  async getSummery() {
    startStep('print summery text');
    let list = await this.summaryText;
    let summary = "";
    for (let i = 0; i < list.length; i++) {
      summary += await BasePage.getText(list[i]);
    }
    endStep();
    return summary;
  }

  //----------------------------------------------------------
  //Actions 
  //----------------------------------------------------------

  async dataFromMainSubTitleArticle() {
    let list = await this.subArticlesButton;
    for (let i = 0; i < list.length; i++) {
      startStep(" clicking on article number " + (i + 1));
      await BasePage.clickButton(list[i]);
      await this.printData();
      endStep();
    }
  }
  async dataFromPoliticalArticle() {
    let list = await this.politicalAndSecurityButton;
    for (let i = 0; i < 3; i++) {
      startStep(" clicking on article number " + (i + 1));
      await BasePage.clickButton(list[i]);
      await this.printData();
      endStep();
    }
  }
  async dataFromIsrealNewsArticle() {
    let list = await this.isrealNewsButton;
    for (let i = 0; i < 1; i++) {
      startStep(" clicking on article number " + (i + 1));
      await BasePage.clickButton(list[i]);
      await this.printData();
      endStep();
    }
  }
  async dataFromWorldNewsArticle() {
    let list = await this.worldNewsButton;
    for (let i = 0; i < 1; i++) {
      startStep(" clicking on article number " + (i + 1));
      await BasePage.clickButton(list[i]);
      await this.printData();
      endStep();
    }
  }
  async printData() {
    startStep("printing the data of the article");
    console.log("title is: " + (await this.getTitle()));
    console.log("img link: " + (await this.getImg()));
    console.log("sub title: " + (await this.getSubTitle()));
    console.log("date: " + (await this.getDate()));
    console.log("summery : " + (await this.getSummery()));
    console.log("-----------------------------------------------------------------------------------------------------------------------------------------------");
    endStep();
    startStep("back to home page");
    await browser.back();
    endStep();
    await browser.pause(4000);
  }
}
module.exports = new ynetNewsPage();
//# sourceMappingURL=data:application/json;charset=utf-8;base64,

